import React from 'react';

const api = 'http://localhost:3000/api/v1/login'

class LoginForm extends React.Component {

    state = { 
        username: "",
        password: "",
        latitude: 0.0,
        longitude: 0.0
     }

    formHandler = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    loginFormSubmission = (e) => {
        e.preventDefault()
        fetch(api, {
            method: 'POST',
            headers: {
                'Content-type': 'application/json',
                Accepts: 'application/json'
            },
            body: JSON.stringify(this.state)
        }).then(res => res.json()).then(user => {
            if (user.errors) {
                alert(user.errors)
            } else {
                //console.log(user.leads)
                this.props.setCurrentUser(user)
            }
        })   
    }

    componentDidMount() {
        const options = {
            enableHighAccuracy: true,
            timeout: 5000,
            maximumAge:0
        }
        const success = (position) => {
            const coordinateObj = position.coords
            this.setState(
                { 
                    latitude: coordinateObj.latitude,  
                    longitude: coordinateObj.longitude
                })
        }
        const error = (err) => {
            console.warn(`ERROR'(${err.code})): ${err.message}`)
        }
    }


    render() { 
        // console.log(this.props)
        return ( 
            <div className="login-form-div">
                <form
                onSubmit={this.loginFormSubmission} 
                className="login-form">

                    <label>Username:
                        <input type="text" 
                        name="username"
                        value={this.state.username}
                        onChange={this.formHandler}
                        /></label><br/><br/>


                    <label>Password:                
                        <input type="password"
                        value={this.state.password}
                        onChange={this.formHandler}
                        name="password" /></label><br/><br/>


                <input type="submit" value="Submit" />  
                </form>
            </div>
         );
    }
}

export default LoginForm;
