import React, { Component } from 'react';
import ReactMapGL, {Marker} from 'react-map-gl'


const TOKEN = process.env.REACT_APP_MAPBOX_TOKEN

class Map extends Component {

    state = { 
        viewport: {
            width: 700,
            height: 250,
            latitude: 40.7577,
            longitude: -72.4376,
            zoom: 12
        }
    }

    componentDidMount() {
        this.getLeadLocation()
    }

    getLeadLocation = () => {
        this.setState(
            {   
                viewport: {...this.state.viewport, latitude: parseFloat(this.props.lead.latitude), longitude: parseFloat(this.props.lead.longitude)}
            })
    }



    render() { 
        // console.log(this.props.lead.latitude)
        // console.log(this.props.lead.longitude)
        // console.log(this.state)

        return ( 
                <ReactMapGL
                    mapboxApiAccessToken={TOKEN}
                    mapStyle='mapbox://styles/mapbox/navigation-guidance-day-v4'
                    {...this.state.viewport}
                    onViewportChange={(viewport) => this.setState({viewport})}>
                        <Marker latitude={this.props.lead.latitude} longitude={this.props.lead.longit}>

                        </Marker>

                </ReactMapGL>

         )
    }
}

export default Map;
