import React, { Component } from 'react';
import ReactMapGL from 'react-map-gl'


const TOKEN = process.env.REACT_APP_MAPBOX_TOKEN

class Map extends Component {



    componentDidMount() {
        state = { 
            viewport: {
                width: 700,
                height: 300,
                latitude: 40.7577,
                longitude: -72.4376,
                zoom: 8
            }
        }
    }


    getLeadLocation = () => {
        if (this.props.lead.longitude !== this.state.viewport.longitude) {
            this.setState(
                {
                    ...this.state.viewport,
                    latitude: this.props.lead.latitude,
                    longitude: this.props.lead.longitude
                })
        }
    }


    render() { 


        console.log(this.state)
        this.getLeadLocation()
        return ( 


            <ReactMapGL
                mapboxApiAccessToken={TOKEN}
                mapStyle='mapbox://styles/mapbox/navigation-guidance-day-v4'
                {...this.state.viewport}
                onViewportChange={(viewport) => this.setState({viewport})}
                />
         )
    }
}

export default Map;
