import React, { Component } from 'react';
import ReactMapGL, {Marker,  FlyToInterpolator} from 'react-map-gl'
import WebMercatorViewport from 'viewport-mercator-project';



const TOKEN = process.env.REACT_APP_MAPBOX_TOKEN

class Map extends Component {

    state = { 
        viewport: {
            width: 700,
            height: 250,
            latitude: 40.7577,
            longitude: -72.4376,
            zoom: 12
        }
    }

    componentDidMount() {
        this.getLeadLocation()
    }

    getLeadLocation = () => {
        this.setState(
            {   
                viewport: {...this.state.viewport, latitude: parseFloat(this.props.lead.latitude), longitude: parseFloat(this.props.lead.longitude)}
            })
    }

     _getPositions = () => {
        const {longitude, latitude, zoom} = new WebMercatorViewport(this.state.viewport)
        .fitBounds([[parseFloat(this.props.lead.longitude), parseFloat(this.props.lead.latitude)], [parseFloat(this.props.user.longitude), parseFloat(this.props.user.longitude)]])
        const viewport = {
            ...this.state.viewport,
            longitude,
            latitude,
            zoom,
            transitionDuration: 5000,
            transitionInterpolator: new FlyToInterpolator(),

        }
        this.setState({viewport})
     }



    render() { 
        // console.log(this.props.lead.latitude)
        // console.log(this.props.lead.longitude)
        // console.log(this.state)


        return ( 
                <ReactMapGL


                    mapboxApiAccessToken={TOKEN}
                    mapStyle='mapbox://styles/mapbox/navigation-guidance-day-v4'
                    {...this.state.viewport}
                    onViewportChange={(viewport) => this.setState({viewport})}

                    >
       <button onClick={this._getPositions}>New York City</button>
                     <Marker latitude={parseFloat(this.props.lead.latitude)} longitude={parseFloat(this.props.lead.longitude)}>
                            x
                        </Marker>
                        <Marker latitude={parseFloat(this.props.user.latitude)} longitude={parseFloat(this.props.user.longitude)}>
                            y
                        </Marker>


                </ReactMapGL>

         )
    }
}

export default Map;
