import React from 'react';
import moment from 'moment'
import { Polar } from 'react-chartjs-2'

class WeeklyPresentationMetric extends React.Component {

    state = { 
        currentDate: moment(),
        currentUser: this.props.currentUser,
        appointments: this.props.appointments,
        weekAppointments: [],
        presentations: [],
        data: {}
    }

    setPolarChartData = () => {
        const polarChartData = {
            // labels: this.state.daysOfWeek,
            datasets: [
                {
                    label: `$Weekly Presentation`,
                    data: [this.state.weekAppointments, this.state.presentations],
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)',
                        'rgba(75, 192, 192, 0.2)',
                        'rgba(153, 102, 255, 0.2)'
                    ],
                    borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)'
                    ],
                    borderWidth: 1
                }]
        }
        return polarChartData
    }



    componentDidUpdate(prevState){
        if (prevState.currentUser !== this.props.currentUser) {
            this.setState(
                { 
                    currentUser: this.props.currentUser,
                    appointments: this.props.appointments,

            }, () => this.setState({ presentations: this.getWeeklyPresentations() }))
        }
    } 



    getWeeklyPresentations = () => {
        let filteredAppointmentsByWeek = this.state.appointments.filter(appointment => moment(appointment.date).isSame(this.state.currentDate, 'week'))
        this.setState({ weekAppointments: filteredAppointmentsByWeek  });
        let presentationAppointments = filteredAppointmentsByWeek.filter(appointment => appointment['presentation_made?'])
        return presentationAppointments
    }


    render() { 
        // console.log('i am props', this.props)
        console.log('i am state',this.state)
        // console.log('filterAppp' , this.getWeeklyPresentations())
        return ( 
            <div className="weekly-presentation-metric">
                <h2>Weekly Presentations</h2>
                <Polar 
                        data={this.setPolarChartData()}
                        options={{
                            scales: {
                                yAxes: [{
                                    display: true,
                                    ticks: {
                                        beginAtZero: true,
                                        stepSize: 1
                                    }
                                }]
                            }
                        }}
                    />
            </div>
        );
    }
}

export default WeeklyPresentationMetric;
