import React, { Component } from 'react';
import moment from 'moment'
import { Bar, Line, Pie} from 'react-chartjs-2'

class Metrics extends Component {
    state = { 
        currentUser: this.props.currentUser,
        currentDate: moment(),
        appointmentDates: [],
        filteredDatesByWeek: [],
        daysOfWeek: [],
        countOfApptInDaysOfWeek: []
    }

    componentDidMount() {
        let newDates, filteredDatesByWeek
        //take all appointments
        newDates = this.props.currentUser.appointments.map(appointment => appointment.date)
        //take appointments and filter them by current week
        //compare today's date with the array of appointments
        filteredDatesByWeek = newDates.filter(date => moment(date).isSame(this.state.currentDate, 'week'))
        this.setState(
            { 
                appointmentDates: [...this.state.appointmentDates, ...newDates],
                filteredDatesByWeek: [...this.state.filteredDatesByWeek, ...filteredDatesByWeek],
                daysOfWeek: [...this.state.daysOfWeek, ...this.getDaysOfTheWeek()],
            }, () => this.setState({countOfApptInDaysOfWeek: [...this.state.countOfApptInDaysOfWeek, ...this.countOfApptInDaysOfWeek(this.state.daysOfWeek, this.state.filteredDatesByWeek)]  }))

    }

    getDaysOfTheWeek = () => {
        let beginningOfWeek = moment().startOf('week')
        let daysOfWeek = []
        //used knowledge of today's date and the start of the week to iterate days to the end of the week
        daysOfWeek.push(beginningOfWeek.format('YYYY-MM-DD'))
        for (let i = 1; i < 7; i++) {
            daysOfWeek.push(beginningOfWeek.add(1, 'days').format('YYYY-MM-DD'))
        }
        return daysOfWeek
    }

    // console.log(moment().isSame(moment('2019-12-23'),'date'))
    countOfApptInDaysOfWeek = (a, b) => {
        let counts = []
        for (let i = 0; i < a.length; i++) {
            let count = 0;
            for (let j = 0; j < b.length; j++) {
                if (moment(b[j]).isSame(moment(a[i]))) {
                    count++
                }
            }
            counts.push(count)
        }
        return counts
    }

    setChartData = () => {
        const chartData = {
            labels: this.state.daysOfWeek,
            datasets: [
                {
                    label: 'Appointments per week',
                    data: this.state.countOfApptInDaysOfWeek,
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                        'rgba(54, 162, 235, 0.2)',
                        'rgba(255, 206, 86, 0.2)',
                        'rgba(75, 192, 192, 0.2)',
                        'rgba(153, 102, 255, 0.2)'
                    ],
                    borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)',
                        'rgba(255, 206, 86, 1)',
                        'rgba(75, 192, 192, 1)',
                        'rgba(153, 102, 255, 1)'
                    ],
                    borderWidth: 1
                }]
        }

    }


    render() {

        console.log('i am the state ',this.state)
        // console.log('i am the daysOfWeek ',this.state.daysOfWeek[0])
        // console.log(this.state.currentDate.toDate())

        // let beginningOfWeek = moment().startOf('week').add(6, 'days').toDate()
        // let endOfWeek = moment().endOf('week').date()
        // console.log('i am the days of the week ',this.getDaysOfTheWeek())
        // console.log(this.countOfApptInDaysOfWeek(this.state.daysOfWeek, this.state.filteredDatesByWeek))



        return ( 
            <div className="metrics-page">
                <h1>Metrics</h1>
                {/* <Bar 
                    data={

                    }
                /> */}
            </div>
        );
    }
}

export default Metrics;
